import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
      {name: 'Zakwan', age: 25},
      {name: 'Shehjad', age: 26},
      {name: 'Yahya', age: 27}
    ],
    showPersons: false
  }

  mySwitchHandler = (newName) => {
    // console.log("Was Clicked!");
    this.setState ({
      persons: [
        {name: newName, age: 25},
        {name: 'Shehjad Bhaiyat', age: 26},
        {name: 'Yahya Bhaiyat', age: 27}
      ]
    })
  }

  myEventHandler = (event) =>{
    this.setState ({
      persons: [
        {name: 'Zakwan Bhaiyat', age: 25},
        {name: event.target.value, age: 26},
        {name: 'Yahya Bhaiyat', age: 27}
      ]
    })
  }

  togglePersonHandler = () => {
    const doesShow = this.state.showPersons;
    this.setState({showPersons: !doesShow})
  }
  render() {
    const buttonStyle = {
      backgroundColor: 'white',
      font: 'inherit',
      border: '1px solid blue',
      padding: '8px',
      cursor: 'pointer'
    }

    let persons = null;

    if(this.state.showPersons){
        persons =(
        <div>
            <Person 
                name = {this.state.persons[0].name} 
                age={this.state.persons[0].age} 
                click={this.mySwitchHandler.bind(this, 'Zakku!!')}>I'm getting better at React </Person>
            <Person 
                name = {this.state.persons[1].name} 
                age ={this.state.persons[1].age} 
                changed ={this.myEventHandler}
                />
            <Person 
                name = {this.state.persons[2].name} 
                age = {this.state.persons[2].age} > 
                I'm learning !</Person>
        </div>
        );
    }

    return (
      <div className="App">
       <h1> Hi, I'm a React App </h1>
       <p> This is awesome </p>
       <button 
          style = {buttonStyle}
          onClick = {this.togglePersonHandler}> Toggle Persons 
       </button>
       {persons}
      </div>
    );
    // return React.createElement('div',{className: 'App'}, React.createElement('h1', null, 'Does this work now?'));
  }
}


export default App;


